# Python program to find the factorial of a number provided by the user. import time #print(\'FACTORIAL\') def main(): # create parrot1 object # change the value for a different result num = 15 # To take input from the user # num = int(input("Enter a number: ")) factorial = 1 # check if the number is negative, positive or zero if num < 0: print("Sorry, factorial does not exist for negative numbers") elif num == 0: print("The factorial of 0 is 1") else: for i in range(1, num + 1): factorial = factorial * i time.sleep(0.3) print(factorial)
print("The factorial of", num, "is", factorial)
if __name__ == "__main__": main()
